import React, { useState, useEffect } from 'react';
import './Timer.scss';

const Timer = () => {
  const [timeLeft, setTimeLeft] = useState(15 * 60);
  const [isRunning, setIsRunning] = useState(false);
  const [activeStep, setActiveStep] = useState(null);
  const [isSoundEnabled, setSoundEnabled] = useState(true);

  // Built-in beep sound
  const beepSound = new Audio("data:audio/mpeg;base64,");

  const steps = [
    { time: 14.83, action: '–î–æ–±–∞–≤—å—Ç–µ –º–∞—Å–ª–æ (150–≥)', type: 'oil' },
    { time: 14, action: '–î–æ–±–∞–≤—å—Ç–µ –ª—É–∫ (200–≥)', type: 'onion' },
    { time: 12, action: '–î–æ–±–∞–≤—å—Ç–µ –±–æ–ª–≥–∞—Ä—Å–∫–∏–π –ø–µ—Ä–µ—Ü (400–≥)', type: 'pepper' },
    { time: 9, action: '–î–æ–±–∞–≤—å—Ç–µ –ø–æ–º–∏–¥–æ—Ä—ã (500–≥)', type: 'tomato' },
    { time: 7, action: '–î–æ–±–∞–≤—å—Ç–µ —Ç–æ–º–∞—Ç–Ω—É—é –ø–∞—Å—Ç—É (200–≥)', type: 'paste' },
    { time: 5, action: '–î–æ–±–∞–≤—å—Ç–µ –≤–æ–¥—É (400–≥)', type: 'water' },
    { time: 4, action: '–î–æ–±–∞–≤—å—Ç–µ —Å–æ–ª—å (20–≥)', type: 'salt' },
    { time: 3, action: '–î–æ–±–∞–≤—å—Ç–µ —Ñ–∞—Å–æ–ª—å (2–∫–≥)', type: 'beans' },
    { time: 0, action: '–ë–ª—é–¥–æ –≥–æ—Ç–æ–≤–æ!', type: 'done' }
  ];

  useEffect(() => {
    let interval = null;
    if (isRunning && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft(time => time - 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isRunning, timeLeft]);

  useEffect(() => {
    const minutes = timeLeft / 60;
    const step = steps.find(s => s.time === Math.floor(minutes * 100) / 100);
    
    if (step) {
      setActiveStep(step.type);
      // Play sound if enabled
      if (isSoundEnabled) {
        beepSound.play().catch(err => console.log('Sound play failed:', err));
        // Play twice more with delay
        setTimeout(() => {
          beepSound.currentTime = 0;
          beepSound.play().catch(err => console.log('Sound play failed:', err));
        }, 500);
        setTimeout(() => {
          beepSound.currentTime = 0;
          beepSound.play().catch(err => console.log('Sound play failed:', err));
        }, 1000);
      }
      setTimeout(() => {
        setActiveStep(null);
      }, 4000);
    }
  }, [timeLeft, isSoundEnabled]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="timer">
      <div className="timer__header">
        <h1>–¢–∞–π–º–µ—Ä –ø—Ä–∏–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è</h1>
        <div className="timer__display">
          {formatTime(timeLeft)}
        </div>
      </div>

      <div className="timer__controls">
        <button
          className={`timer__button ${isRunning ? 'timer__button--disabled' : ''}`}
          onClick={() => setIsRunning(true)}
          disabled={isRunning}
        >
          –ù–∞—á–∞—Ç—å
        </button>
        <button
          className="timer__button timer__button--reset"
          onClick={() => {
            setIsRunning(false);
            setTimeLeft(15 * 60);
            setActiveStep(null);
          }}
        >
          –°–±—Ä–æ—Å–∏—Ç—å
        </button>
        <button
          className={`timer__button timer__button--sound ${!isSoundEnabled ? 'timer__button--sound-off' : ''}`}
          onClick={() => setSoundEnabled(!isSoundEnabled)}
        >
          {isSoundEnabled ? 'üîä' : 'üîà'}
        </button>
      </div>

      <div className="timer__steps">
        {steps.map((step, index) => {
          const minutes = timeLeft / 60;
          const isCompleted = minutes < step.time;
          const isActive = step.type === activeStep;

          return (
            <div
              key={index}
              className={`
                timer__step
                ${isCompleted ? 'timer__step--completed' : ''}
                ${isActive ? 'timer__step--active' : ''}
              `}
            >
              <div className="timer__step-time">{step.time} –º–∏–Ω</div>
              <div className="timer__step-action">{step.action}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Timer;